package com.hck.getmoney.adpter;import java.util.List;import android.content.Context;import android.view.LayoutInflater;import android.view.View;import android.view.ViewGroup;import android.widget.BaseAdapter;import android.widget.TextView;import com.hck.kedouzq.R;import com.hck.getmoney.bean.OrderBean;import com.hck.getmoney.bean.TuiGBean;public class TGAdpter extends BaseAdapter{   private List<TuiGBean> beans;   private Context context;   public TGAdpter(List<TuiGBean> beans,Context context)   {	   this.beans=beans;	   this.context=context;	      }	@Override	public int getCount() {		return beans.size();	}	@Override	public Object getItem(int arg0) {		return beans.get(arg0);	}	@Override	public long getItemId(int arg0) {		return arg0;	}	@Override	public View getView(int position, View convertView, ViewGroup arg2) {		try {			convertView=LayoutInflater.from(context).inflate(R.layout.listview_tg_item, null);	        Holder holder=null;	        holder=new Holder();	        holder.contentTextView=(TextView) convertView.findViewById(R.id.dh_text2);	        holder.timTextView=(TextView) convertView.findViewById(R.id.dh_text3);	        String content=beans.get(position).getContent();	        holder.contentTextView.setText(content.substring(content.indexOf("h")+1, content.length()));	        holder.timTextView.setText(beans.get(position).getTime().substring(0,11));	        holder.nameTextView=(TextView)convertView.findViewById(R.id.tg_name);	        holder.nameTextView.setText(beans.get(position).getUserName());		} catch (Exception e) {		}		return convertView;	}	static class Holder {        TextView contentTextView;        TextView timTextView;        TextView nameTextView;	}}