package com.hck.getmoney.bean;public class ShareBean {	private String content;	private String urlString;	private String imageUrl;	public String getContent() {		return content;	}	public void setContent(String content) {		this.content = content;	}	public String getUrlString() {		return urlString;	}	public void setUrlString(String urlString) {		this.urlString = urlString;	}	public String getImageUrl() {		return imageUrl;	}	public void setImageUrl(String imageUrl) {		this.imageUrl = imageUrl;	}}