package com.hck.getmoney.ui;import org.json.JSONObject;import android.os.Bundle;import android.util.Log;import android.view.View;import android.widget.TextView;import com.baidu.mobads.appoffers.OffersManager;import com.baidu.mobads.appoffers.PointsUpdateListener;import com.hck.getmoney.data.Data;import com.hck.getmoney.data.HttpUrls;import com.hck.getmoney.net.JsonHttpResponseHandler;import com.hck.getmoney.net.RequestParams;import com.hck.getmoney.util.HttpUtil;import com.hck.getmoney.widget.Toasts;import com.hck.kedouzq.R;/** *  * @author *  */public class BaiduActivity extends BaseActivity {	private int num;	private String content;	private int point;	private TextView nameTextView, contenTextView;	private int kid;    private int count =0;	@Override	protected void onCreate(Bundle savedInstanceState) {		super.onCreate(savedInstanceState);		initAd();		setContentView(R.layout.activity_play_dl);		initView();		num = getIntent().getIntExtra("num", 5);		content = getIntent().getStringExtra("neirong");		kid=getIntent().getIntExtra("kid", 0);		initDate();	}	private void initAd() {		try {			OffersManager.setPointsUpdateListener(this, new PointsUpdateListener() {								@Override				public void onPointsUpdateSuccess(int arg0) {					point = 0;					money =0;					point = arg0;					money =arg0;										if (point > 1000) {						point=350;					}					if(point> 0){						savePoint();					}									}								@Override				public void onPointsUpdateFailed(String arg0) {									}			});		} catch (Exception e) {		}			}	private void initView() {		nameTextView = (TextView) findViewById(R.id.play_dl_name);		contenTextView = (TextView) findViewById(R.id.play_dl_content);	}	private void initDate() {		nameTextView.setText("本区每天限制下载试用" + num + "个软件");		contenTextView.setText(content);	}	public void startGetMoney(View view) {		count =0;		try {			OffersManager.showOffers(this);		} catch (Exception e) {		}			}	private void savePoint() {		point=hashYQM(point);		params = new RequestParams();		params.put("kindid", kid + "");		params.put("num", num + "");		params.put("type", "百度");		if (Data.userBean == null) {			Toasts.toast(this, "增加金币异常  请重新登录");			return;		}		params.put("uid", Data.userBean.getId() + "");		params.put("money", point + "");		HttpUtil.getHttpUtil().get(HttpUrls.addMoneyP, params,				new JsonHttpResponseHandler() {					@Override					public void onFinish(String url) {						super.onFinish(url);					}					@Override					public void onFailure(Throwable error, String content) {						super.onFailure(error, content);						Toasts.toast(getApplicationContext(), "网络异常 增加金币失败 ");					}					@Override					public void onSuccess(int statusCode, JSONObject response) {						super.onSuccess(statusCode, response);                             huafei(money);						try {							isOk = response.getBoolean("isok");							if (isOk) {								Toasts.toast(BaiduActivity.this, nt);								Data.userBean.setAllKeDouBi(Data.userBean										.getAllKeDouBi() + point);								Log.d("hck", "获取金币成功 ");							} else {								if (response.getInt("type") == 1) {									Toasts.toast(BaiduActivity.this,											"获取金币失败 本区每天限制下载" + num + "个应用  ");								} else {									Toasts.toast(getApplicationContext(),											"网络异常 增加金币失败 ");								}							}						} catch (Exception e) {							Toasts.toast(BaiduActivity.this, "网络异常 增加金币失败 ");							Log.d("hck", "eeeeeeeeeeeee: "+e.toString());						}					}				});	}		private void huafei(int point){		try {			OffersManager.subPoints(this, point);		} catch (Exception e) {			if (count<3) {				OffersManager.subPoints(this, point);			}			count++;					}			}	@Override	protected void onDestroy() {		super.onDestroy();	}	@Override	protected void onResume() {		try {			OffersManager.getPoints(this);					} catch (Exception e) {		}		super.onResume();			}}