package com.hck.getmoney.ui;import org.json.JSONObject;import android.os.Bundle;import android.os.Handler;import android.util.Log;import android.view.View;import android.widget.TextView;import cn.aow.android.DAOW;import com.bb.dd.BeiduoPlatform;import com.bb.dd.listener.IReduceListener;import com.dc.wall.DianCai;import com.dc.wall.IEarnMoneyNotifier;import com.dc.wall.IQueryMoneyNotifier;import com.dc.wall.IReduceMoneyNotifier;import com.hck.getmoney.data.Data;import com.hck.getmoney.data.HttpUrls;import com.hck.getmoney.net.JsonHttpResponseHandler;import com.hck.getmoney.net.RequestParams;import com.hck.getmoney.util.HttpUtil;import com.hck.getmoney.widget.Toasts;import com.hck.kedouzq.R;/** *  * @author *  */public class DianCaiActivity extends BaseActivity {	private boolean isFirst = true;	private int num;	private String content;	private int point;	private TextView nameTextView, contenTextView;	private int kid;	@Override	protected void onCreate(Bundle savedInstanceState) {		super.onCreate(savedInstanceState);		initAd();		setContentView(R.layout.activity_play_dl);		initView();		kid = getIntent().getIntExtra("kid", 0);		num = getIntent().getIntExtra("num", 5);		content = getIntent().getStringExtra("neirong");		initDate();			}	private void initAd() {		DianCai.initApp(this, "11033", "0380d21f6ca24f30838b77e416decb2c");		DianCai.setEarnMoneyListener(new IEarnMoneyNotifier() {			@Override			public void earnSuccess(int arg0, int arg1, String arg2) {				if (arg1 > 0) {					handler.sendEmptyMessage(arg1);				}			}			@Override			public void earnFailed(int arg0, int arg1, String arg2) {			}		});	}	private void initView() {		nameTextView = (TextView) findViewById(R.id.play_dl_name);		contenTextView = (TextView) findViewById(R.id.play_dl_content);	}	private void initDate() {		nameTextView.setText("本区每天限制下载试用" + num + "个软件");		contenTextView.setText(content);	}	public void startGetMoney(View view) {		DianCai.showOfferWall();	}	Handler handler = new Handler() {		public void handleMessage(android.os.Message msg) {			point=0;			point = msg.what;			money= 0;			money= point;			if (point > 500) {								point = 150;			}			if (point > 0) {				savePoint();			}			huafei(point);		};	};	private void savePoint() {		point= hashYQM(point);		params = new RequestParams();		params.put("kindid", kid + "");		params.put("num", num + "");		params.put("type", "点财");		if (Data.userBean == null) {			Toasts.toast(this, "增加金币异常  请重新登录");			return;		}		params.put("uid", Data.userBean.getId() + "");		params.put("money", point + "");		HttpUtil.getHttpUtil().get(HttpUrls.addMoneyP, params,				new JsonHttpResponseHandler() {					@Override					public void onFinish(String url) {						super.onFinish(url);						huafei(money);					}					@Override					public void onFailure(Throwable error, String content) {						super.onFailure(error, content);						Toasts.toast(getApplicationContext(), "网络异常 增加金币失败 ");					}					@Override					public void onSuccess(int statusCode, JSONObject response) {						super.onSuccess(statusCode, response);												try {							isOk = response.getBoolean("isok");							if (isOk) {								Toasts.toast(getApplicationContext(),nt										);								Data.userBean.setAllKeDouBi(Data.userBean										.getAllKeDouBi() + point);							} else {								if (response.getInt("type") == 1) {									Toasts.toast(getApplicationContext(),											"获取金币失败 本区每天限制下载" + num + "个应用  ");								} else {									Toasts.toast(getApplicationContext(),											"网络异常 增加金币失败 ");								}							}						} catch (Exception e) {							Toasts.toast(DianCaiActivity.this, "网络异常 增加金币失败 ");						}					}				});	}	private void huafei(final int point) {		DianCai.reduceMoney(point, new IReduceMoneyNotifier() {			@Override			public void reduceSuccess(int arg0) {			}			@Override			public void reduceFailed(int arg0) {			}		});	}		@Override	protected void onResume() {		super.onResume();		try {			DianCai.queryMoney(new IQueryMoneyNotifier() {								@Override				public void querySuccess(int arg0, String arg1) {					handler.sendEmptyMessage(arg0);				}								@Override				public void queryFailed(int arg0) {									}			});		} catch (Exception e) {		}			}}