package com.hck.getmoney.ui;import android.app.Activity;import android.content.Intent;import android.os.Bundle;import android.view.KeyEvent;import android.widget.FrameLayout;import com.hck.kedouzq.R;import com.qq.e.splash.SplashAd;import com.qq.e.splash.SplashAdListener;public class Splash1 extends Activity {	@Override	protected void onCreate(Bundle savedInstanceState) {		super.onCreate(savedInstanceState);		setContentView(R.layout.activity_fullscreen);		FrameLayout container = (FrameLayout) this				.findViewById(R.id.splashcontainer);		new SplashAd(this, container, "1104666314", "1000300468376188",				new SplashAdListener() {					@Override					public void onAdPresent() {					}					@Override					public void onAdFailed(int arg0) {						startLoading();					}					@Override					public void onAdDismissed() {						startLoading();					}				});	}	@Override	public boolean onKeyDown(int keyCode, KeyEvent event) {		if (keyCode == KeyEvent.KEYCODE_BACK				|| keyCode == KeyEvent.KEYCODE_HOME) {			return true;		}		return super.onKeyDown(keyCode, event);	}	private void startLoading() {		startActivity(new Intent(this, LodingActivity.class));		finish();	}}