package com.hck.getmoney.ui;import com.hck.kedouzq.R;import android.app.Activity;import android.os.Bundle;import android.view.View;import android.webkit.WebView;import android.widget.Button;import android.widget.TextView;public class XinShouBiKanActivity extends Activity{	TextView textView;	WebView webView;	Button button;	@Override	protected void onCreate(Bundle savedInstanceState) {		super.onCreate(savedInstanceState);		setContentView(R.layout.activiiti_xinshoubikan);		textView = (TextView) findViewById(R.id.title_text);		textView.setText("新手必读");		webView =(WebView) findViewById(R.id.webView);		webView.loadUrl("file:///android_asset/xinshou.html");		button =(Button) findViewById(R.id.title_bt_left);		button.setVisibility(View.VISIBLE);	}	public void back(View view){		finish();	}}