package com.hck.zhuanqian.adpter;import java.util.List;import android.content.Context;import android.view.LayoutInflater;import android.view.View;import android.view.ViewGroup;import android.widget.BaseAdapter;import android.widget.TextView;import com.hck.zhuanqian.R;import com.hck.zhuanqian.bean.OrderBean;public class NoChuLiOrderAdpter extends BaseAdapter{   private List<OrderBean> orderBeans;   private Context context;   public NoChuLiOrderAdpter(List<OrderBean> beans,Context context)   {	   this.orderBeans=beans;	   this.context=context;	      }	@Override	public int getCount() {		return orderBeans.size();	}	@Override	public Object getItem(int arg0) {		return orderBeans.get(arg0);	}	@Override	public long getItemId(int arg0) {		return arg0;	}	@Override	public View getView(int position, View convertView, ViewGroup arg2) {		try {			convertView=LayoutInflater.from(context).inflate(R.layout.listview_myorder_item, null);	        Holder holder=null;	        holder=new Holder();	        holder.contentTextView=(TextView) convertView.findViewById(R.id.dh_text2);	        holder.timTextView=(TextView) convertView.findViewById(R.id.dh_text3);	        String content=orderBeans.get(position).getContent();	        holder.contentTextView.setText(content.replace("h", ""));	        holder.timTextView.setText(orderBeans.get(position).getTime().substring(0,11));		} catch (Exception e) {		}		return convertView;	}	static class Holder {        TextView contentTextView;        TextView timTextView;	}}