package com.hck.zhuanqian.bean;public class KindBean {	private int id;	private String content;	private int isOk;	private int num;	private String neirong;	private int aid;	public int getAid() {		return aid;	}	public void setAid(int aid) {		this.aid = aid;	}	public String getNeirong() {		return neirong;	}	public void setNeirong(String neirong) {		this.neirong = neirong;	}	public int getNum() {		return num;	}	public void setNum(int num) {		this.num = num;	}	public int getId() {		return id;	}	public void setId(int id) {		this.id = id;	}	public String getContent() {		return content;	}	public void setContent(String content) {		this.content = content;	}	public int getIsOk() {		return isOk;	}	public void setIsOk(int isOk) {		this.isOk = isOk;	}}