package com.hck.zhuanqian.bean;public class TuiGBean {	private String content;	private String time;	private String userName;	private long id;	public long getId() {		return id;	}	public void setId(long id) {		this.id = id;	}	public String getUserName() {		return userName;	}	public void setUserName(String userName) {		this.userName = userName;	}	public String getContent() {		return content;	}	public void setContent(String content) {		this.content = content;	}	public String getTime() {		return time;	}	public void setTime(String time) {		this.time = time;	}}