package com.hck.zhuanqian.ui;import android.os.Bundle;import android.text.Html;import android.view.View;import android.webkit.WebView;import android.widget.Button;import android.widget.TextView;import com.hck.zhuanqian.R;public class AboutActivity extends BaseActivity{	private TextView textView;	private Button button;	private WebView contentTextView;	@Override	protected void onCreate(Bundle savedInstanceState) {		super.onCreate(savedInstanceState);		setContentView(R.layout.activity_about);		textView=(TextView) findViewById(R.id.title_text);		findViewById(R.id.title_bt_left).setVisibility(View.VISIBLE);		textView.setText("关于软件（必看）");		button=(Button) findViewById(R.id.title_bt_left);		contentTextView=(WebView) findViewById(R.id.webView_about);		contentTextView.loadUrl("file:///android_asset/1.html");	}	public void back(View view)	{		this.finish();	}}