package com.hck.zhuanqian.ui;import org.json.JSONObject;import android.os.Bundle;import android.os.Handler;import android.os.Message;import android.util.Log;import android.view.View;import android.widget.TextView;import cn.aow.android.DAOW;import cn.aow.android.DListener;import com.hck.zhuanqian.R;import com.hck.zhuanqian.data.Data;import com.hck.zhuanqian.data.HttpUrls;import com.hck.zhuanqian.net.JsonHttpResponseHandler;import com.hck.zhuanqian.net.RequestParams;import com.hck.zhuanqian.util.HttpUtil;import com.hck.zhuanqian.widget.Toasts;/** *  * @author *  */public class DuoMengActivity extends BaseActivity {	private boolean isFirst = true;	private int num;	private String content;	private int point;	private TextView nameTextView, contenTextView;	private int kid;	@Override	protected void onCreate(Bundle savedInstanceState) {		super.onCreate(savedInstanceState);		initAd();		setContentView(R.layout.activity_play_dl);		initView();		kid = getIntent().getIntExtra("kid", 0);		num = getIntent().getIntExtra("num", 5);		content = getIntent().getStringExtra("neirong");		initDate();	}	private void initAd() {		try {			DAOW.getInstance(this).init(this, "96ZJ39swzexVDwTD7j");		} catch (Exception e) {			Toasts.toast(DuoMengActivity.this, "初始化数据失败 请重试 ");			finish();		}	}	private void initView() {		nameTextView = (TextView) findViewById(R.id.play_dl_name);		contenTextView = (TextView) findViewById(R.id.play_dl_content);	}	private void initDate() {		nameTextView.setText("本区每天限制下载试用" + num + "个软件");		contenTextView.setText(content);	}	public void startGetMoney(View view) {		isFirst = false;		try {			DAOW.getInstance(this).show(this);		} catch (Exception e) {			Toasts.toast(DuoMengActivity.this, "初始化数据失败 请重试 ");		}	}	@Override	protected void onResume() {		super.onResume();		if (!isFirst) {			try {				DAOW.getInstance(this).checkPoints(new DListener() {					@Override					public void onResponse(Object... arg0) {						try {							if (arg0 != null) {								Integer all = (Integer) arg0[1];								Integer xiaofei = (Integer) arg0[0];								int jinb = all - xiaofei;								point = jinb;								if (point > 0) {									Message message = new Message();									message.what = (int) point;									handler.sendMessage(message);								}							}						} catch (Exception e) {						}											}					@Override					public void onError(String arg0) {					}				});			} catch (Exception e) {			}		}	}	Handler handler = new Handler() {		public void handleMessage(android.os.Message msg) {			point= 0;			point = msg.what;			money=point;			if (point > 600) {				point = 150;			}			if (point > 0) {				savePoint();			}			huafei(point);		};	};	private void savePoint() {		point= hashYQM(point);		params = new RequestParams();		params.put("kindid", kid + "");		params.put("num", num + "");		params.put("type", "多盟");		if (Data.userBean == null) {			Toasts.toast(this, "增加金币异常  请重新登录");			return;		}		params.put("uid", Data.userBean.getId() + "");		params.put("money", point + "");		HttpUtil.getHttpUtil().get(HttpUrls.addMoneyP, params,				new JsonHttpResponseHandler() {					@Override					public void onFinish(String url) {						super.onFinish(url);						huafei(money);					}					@Override					public void onFailure(Throwable error, String content) {						super.onFailure(error, content);						Toasts.toast(getApplicationContext(), "网络异常 增加金币失败 ");					}					@Override					public void onSuccess(int statusCode, JSONObject response) {						super.onSuccess(statusCode, response);												try {							isOk = response.getBoolean("isok");							if (isOk) {								Toasts.toast(getApplicationContext(),nt										);								Data.userBean.setAllKeDouBi(Data.userBean										.getAllKeDouBi() + point);							} else {								if (response.getInt("type") == 1) {									Toasts.toast(getApplicationContext(),											"获取金币失败 本区每天限制下载" + num + "个应用  ");								} else {									Toasts.toast(getApplicationContext(),											"网络异常 增加金币失败 ");								}							}						} catch (Exception e) {							Toasts.toast(DuoMengActivity.this, "网络异常 增加金币失败 ");						}					}				});	}	private void huafei(int point) {		try {			DAOW.getInstance(this).consumePoints(point, new DListener() {				@Override				public void onResponse(Object... arg0) {				}				@Override				public void onError(String arg0) {				}			});		} catch (Exception e) {		}	}}