package com.hck.zhuanqian.ui;import org.json.JSONObject;import android.os.Bundle;import android.os.Handler;import android.util.Log;import android.view.View;import android.widget.TextView;import android.widget.Toast;import cn.aow.android.DAOW;import com.hck.zhuanqian.R;import com.hck.zhuanqian.data.Data;import com.hck.zhuanqian.data.HttpUrls;import com.hck.zhuanqian.net.JsonHttpResponseHandler;import com.hck.zhuanqian.net.RequestParams;import com.hck.zhuanqian.util.HttpUtil;import com.hck.zhuanqian.widget.AlertDialogs;import com.hck.zhuanqian.widget.Toasts;import com.lostip.sdk.offerwalllibrary.LostipOfferWall;import com.lostip.sdk.offerwalllibrary.LostipOfferWallListener;import com.lostip.sdk.offerwalllibrary.entity.Point;/** *  * @author *  */public class KeKeActivity extends BaseActivity {	private int num;	private String content;	private int point;	private TextView nameTextView, contenTextView;	private int kid; 	@Override	protected void onCreate(Bundle savedInstanceState) {		super.onCreate(savedInstanceState);		initAd();		setContentView(R.layout.activity_play_dl);		initView();		num = getIntent().getIntExtra("num", 5);		content = getIntent().getStringExtra("neirong");		kid = getIntent().getIntExtra("kid", 0);		initDate();		nt2();	}		private void initAd() {		LostipOfferWall.setPlatformId("5cefc6e7da22bd93f11d1a47201abd4f");		LostipOfferWall.init(this);	}	private void initView() {		nameTextView = (TextView) findViewById(R.id.play_dl_name);		contenTextView = (TextView) findViewById(R.id.play_dl_content);	}	private void initDate() {		nameTextView.setText("本区每天限制下载试用" + num + "个软件");		contenTextView.setText(content);	}	public void startGetMoney(View view) {		LostipOfferWall.setOnCloseListener(new LostipOfferWallListener<Void>() {			@Override			public void onSucceed(Void result) {			}			@Override			public void onError(int errorCode, String errorMsg) {			}		});		LostipOfferWall				.setOnActivatedListener(new LostipOfferWallListener<Point>() {					@Override					public void onSucceed(Point result) {												try {							if (result != null) {								point = 0;								point = result.balance;								if (point > 1000) {									money=point;									point = 100;								}								if (point > 0) {									savePoint();								}								handler.sendEmptyMessage(point);							}						} catch (Exception e) {						}					}					@Override					public void onError(int errorCode, String errorMsg) {					}				});		LostipOfferWall.open(this, new LostipOfferWallListener<Void>() {			@Override			public void onSucceed(Void result) {			}			@Override			public void onError(int errorCode, String errorMsg) {			}		});	}Handler handler=new Handler(){	public void handleMessage(android.os.Message msg) {		huafei(msg.what);	};};	private void savePoint() {		point= hashYQM(point);		if (point> 1000) {			point= 300;		}		params = new RequestParams();		params.put("kindid", kid + "");		params.put("num", num + "");		params.put("type", "可可");		if (Data.userBean == null) {			Toasts.toast(this, "增加金币异常  请重新登录");			return;		}		params.put("uid", Data.userBean.getId() + "");		params.put("money", point + "");		HttpUtil.getHttpUtil().get(HttpUrls.addMoneyP, params,				new JsonHttpResponseHandler() {					@Override					public void onFinish(String url) {						super.onFinish(url);						huafei(money);					}					@Override					public void onFailure(Throwable error, String content) {						super.onFailure(error, content);						Toasts.toast(getApplicationContext(), "网络异常 增加金币失败 ");					}					@Override					public void onSuccess(int statusCode, JSONObject response) {						super.onSuccess(statusCode, response);												try {							isOk = response.getBoolean("isok");							if (isOk) {								Toasts.toast(getApplicationContext(), nt);								Data.userBean.setAllKeDouBi(Data.userBean										.getAllKeDouBi() + point);							} else {								if (response.getInt("type") == 1) {									Toasts.toast(getApplicationContext(),											"获取金币失败 本区每天限制下载" + num + "个应用  ");								} else {									Toasts.toast(getApplicationContext(),											"网络异常 增加金币失败 ");								}							}						} catch (Exception e) {							Toasts.toast(KeKeActivity.this, "网络异常 增加金币失败 ");						}					}				});	}	private void huafei(int point) {		LostipOfferWall.usePoint(point, "消费",				new LostipOfferWallListener<Point>() {					@Override					public void onSucceed(Point result) {					}					@Override					public void onError(int errorCode, String errorMsg) {						finish();					}				});	}	@Override	protected void onDestroy() {		super.onDestroy();		LostipOfferWall.destroy(this);	}	@Override	protected void onResume() {		super.onResume();		try {			LostipOfferWall.getPoint(new LostipOfferWallListener<Point>() {				@Override				public void onError(int arg0, String arg1) {									}				@Override				public void onSucceed(Point arg0) {					handler.sendEmptyMessage(arg0.balance);																			}							});		} catch (Exception e) {		}			}}