package com.hck.zhuanqian.ui;import java.util.ArrayList;import android.graphics.BitmapFactory;import android.graphics.Matrix;import android.os.Bundle;import android.support.v4.app.Fragment;import android.support.v4.view.ViewPager;import android.support.v4.view.ViewPager.OnPageChangeListener;import android.util.DisplayMetrics;import android.view.View;import android.view.View.OnClickListener;import android.view.animation.Animation;import android.view.animation.TranslateAnimation;import android.widget.Button;import android.widget.ImageView;import android.widget.TextView;import com.hck.zhuanqian.R;import com.hck.zhuanqian.adpter.AppsPagerAdapter;public class TuiGuangActivity extends BaseFragment{	private TextView titleTextView;	private Button backButton;	@Override	protected void onCreate(Bundle savedInstanceState) {		super.onCreate(savedInstanceState);		setContentView(R.layout.activity_tuiguang);		initView();		initPagerViewer();		setListener();			}	private void initView(){		titleTextView =(TextView) findViewById(R.id.title_text);		titleTextView.setText("推广赚钱（推荐）");		backButton =(Button) findViewById(R.id.title_bt_left);		backButton.setVisibility(View.VISIBLE);		views = new ArrayList<Fragment>();		TuiGuangFragment tuiGuangFragment =new TuiGuangFragment();		views.add(tuiGuangFragment);		ShowTgFragment tFragment =new ShowTgFragment();		views.add(tFragment);		textView1 = (TextView) findViewById(R.id.app_tg);		textView2 = (TextView) findViewById(R.id.app_my_tg);		InitImageView();	}	private void InitImageView() {		cursor = (ImageView) findViewById(R.id.cursor);		bmpW = BitmapFactory.decodeResource(getResources(), R.drawable.roller)				.getWidth();		DisplayMetrics dm = new DisplayMetrics();		getWindowManager().getDefaultDisplay().getMetrics(dm);		int screenW = dm.widthPixels;		offset = (screenW / 2 - bmpW) / 2;		Matrix matrix = new Matrix();		matrix.postTranslate(offset, 0);		cursor.setImageMatrix(matrix);	}	private void initPagerViewer() {		pager = (ViewPager) findViewById(R.id.viewpage);		pager.setAdapter(new AppsPagerAdapter(getSupportFragmentManager(),				views));		pager.setCurrentItem(0);		pager.setOnPageChangeListener(new MyOnPageChangeListener());	}	public void setListener() {		textView1.setOnClickListener(new MyOnClickListener(0));		textView2.setOnClickListener(new MyOnClickListener(1));		backButton.setOnClickListener(new OnClickListener() {						@Override			public void onClick(View arg0) {				finish();			}		});	}	public class MyOnPageChangeListener implements OnPageChangeListener {		int one = offset * 2 + bmpW;		@Override		public void onPageSelected(int arg0) {			Animation animation = null;			switch (arg0) {			case 0:				animation = new TranslateAnimation(one, 0, 0, 0);				 textView1.setTextColor(getResources().getColor(R.color.whilt));				 textView2.setTextColor(getResources().getColor(R.color.black));				break;			case 1:				animation = new TranslateAnimation(offset, one, 0, 0);				 textView1.setTextColor(getResources().getColor(R.color.black));				 textView2.setTextColor(getResources().getColor(R.color.whilt));				break;			}			animation.setFillAfter(true);			animation.setDuration(300);			cursor.startAnimation(animation);		}		@Override		public void onPageScrollStateChanged(int arg0) {		}		@Override		public void onPageScrolled(int arg0, float arg1, int arg2) {		}	}	public class MyOnClickListener implements View.OnClickListener {		private int index = 0;		public MyOnClickListener(int i) {			index = i;		}		@Override		public void onClick(View v) {			pager.setCurrentItem(index);		}	};}