package com.hck.zhuanqian.ui;import android.os.Bundle;import android.support.v4.app.Fragment;import android.view.LayoutInflater;import android.view.View;import android.view.ViewGroup;import android.webkit.WebView;import android.widget.TextView;import com.hck.zhuanqian.R;import com.hck.zhuanqian.data.Data;public class TuiGuangFragment extends Fragment {	private View view;	private TextView titleTextView;	private WebView webView;	@Override	public View onCreateView(LayoutInflater inflater, ViewGroup container,			Bundle savedInstanceState) {		view = inflater.inflate(R.layout.tuiguang_fragment, null);		initView();		initData();		return view;	}	private void initView() {		titleTextView = (TextView) view.findViewById(R.id.tuiguang_title);		webView = (WebView) view.findViewById(R.id.tuiguang_webview);	}	private void initData()	{		titleTextView.setText("你的推广码为: "+Data.userBean.getJhm());		webView.loadUrl("file:///android_asset/2.html");	}}