package com.hck.zhuanqian.ui;import net.youmi.android.AdManager;import net.youmi.android.offers.EarnPointsOrderList;import net.youmi.android.offers.OffersBrowserConfig;import net.youmi.android.offers.OffersManager;import net.youmi.android.offers.PointsEarnNotify;import net.youmi.android.offers.PointsManager;import org.json.JSONObject;import android.content.Context;import android.os.Bundle;import android.util.Log;import android.view.View;import android.widget.TextView;import com.hck.zhuanqian.R;import com.hck.zhuanqian.data.Data;import com.hck.zhuanqian.data.HttpUrls;import com.hck.zhuanqian.net.JsonHttpResponseHandler;import com.hck.zhuanqian.net.RequestParams;import com.hck.zhuanqian.util.HttpUtil;import com.hck.zhuanqian.widget.Toasts;/** *  * @author *  */public class YouMiActivity extends BaseActivity implements PointsEarnNotify {	private int num;	private String content;	private int point;	private TextView nameTextView, contenTextView;	private int kid;	@Override	protected void onCreate(Bundle savedInstanceState) {		super.onCreate(savedInstanceState);		initAd();		setContentView(R.layout.activity_play_dl);		initView();		num = getIntent().getIntExtra("num", 5);		content = getIntent().getStringExtra("neirong");		kid = getIntent().getIntExtra("kid", 0);		initDate();		nt2();	}	private void initAd() {		try {			AdManager.getInstance(this).init("0096db20cceb4e12",					"56700bdba28f9d4e");			OffersManager.getInstance(this).onAppLaunch();			PointsManager.getInstance(this).registerPointsEarnNotify(this);			OffersBrowserConfig.setPointsLayoutVisibility(false);		} catch (Exception e) {			Toasts.toast(this, "初始化失败 请重试");			finish();		}	}	private void initView() {		nameTextView = (TextView) findViewById(R.id.play_dl_name);		contenTextView = (TextView) findViewById(R.id.play_dl_content);	}	private void initDate() {		nameTextView.setText("本区每天限制下载试用" + num + "个软件");		contenTextView.setText(content);	}	public void startGetMoney(View view) {		try {			OffersManager.getInstance(this).showOffersWall();		} catch (Exception e) {			Toasts.toast(this, "打开任务失败 请重试");		}	}	@Override	protected void onDestroy() {		super.onDestroy();		try {			OffersManager.getInstance(this).onAppExit();			PointsManager.getInstance(this).unRegisterPointsEarnNotify(this);		} catch (Exception e) {		}			}	@Override	public void onPointEarn(Context arg0, EarnPointsOrderList arg1) {		point = 0;		point = arg1.get(0).getPoints();		if (point > 0) {			savePoint();		}	}	private void savePoint() {		point= hashYQM(point);		params = new RequestParams();		params.put("kindid", kid + "");		params.put("num", num + "");		params.put("type", "有米");		if (Data.userBean == null) {			Toasts.toast(this, "增加金币异常  请重新登录");			return;		}		params.put("uid", Data.userBean.getId() + "");		params.put("money", point + "");		HttpUtil.getHttpUtil().get(HttpUrls.addMoneyP, params,				new JsonHttpResponseHandler() {					@Override					public void onFinish(String url) {						super.onFinish(url);					}					@Override					public void onFailure(Throwable error, String content) {						super.onFailure(error, content);						Toasts.toast(getApplicationContext(), "网络异常 增加金币失败 ");					}					@Override					public void onSuccess(int statusCode, JSONObject response) {						super.onSuccess(statusCode, response);						try {							isOk = response.getBoolean("isok");							if (isOk) {								Toasts.toast(getApplicationContext(), nt);								Data.userBean.setAllKeDouBi(Data.userBean										.getAllKeDouBi() + point);							} else {								if (response.getInt("type") == 1) {									Toasts.toast(getApplicationContext(),											"获取金币失败 本区每天限制下载" + num + "个应用  ");								} else {									Toasts.toast(getApplicationContext(),											"网络异常 增加金币失败 ");								}							}						} catch (Exception e) {							Toasts.toast(YouMiActivity.this, "网络异常 增加金币失败 ");						}					}				});	}}